                "DISP_CLASS": days.indexOf("F") != -1 ? "btn-success" : "btn-default"
            }, {
                "LABEL": "Sat",
                "AVAIL": days.indexOf("S") != -1,
                "DISP_CLASS": days.indexOf("S") != -1 ? "btn-success" : "btn-default"
            }, {
                "LABEL": "Sun",
                "AVAIL": days.indexOf("U") != -1,
                "DISP_CLASS": days.indexOf("U") != -1 ? "btn-success" : "btn-default"
            }];
            if (!value[0].schedule){
                value[0].schedule = [];
            }
            value[0].schedule.push( {
                "DURATION": value[0].DURATION,
                "INSTRUCTOR": value[0].INSTRUCTOR,
                "TIME_DISPLAY": value[0].TIME_DISPLAY,
                "TIME": value[0].TIME,
                "LOCATION": value[0].LOCATION,
                "WEEK_DATA": value[0].WEEK_DATA
            });
            //console.log(value[0].schedule);
            //
            if (prevValue == "") {
                $scope.classes.push(value[0]); 
                prevValue = value[0];
            } else if (prevValue.CRN == value[0].CRN && 
                       prevValue.TERM_CODE == value[0].TERM_CODE &&
                       prevValue.SUBJECT_CODE == value[0].SUBJECT_CODE &&
                       prevValue.CREDIT_HOURS == value[0].CREDIT_HOURS &&
                       prevValue.COURSE_ID == value[0].COURSE_ID) {
                $scope.classes[vindex].schedule.push(value[0].schedule[0]);
            } else {
                vindex++;
                $scope.classes.push(value[0]); 
                prevValue = value[0];                
            } 

                   
        });
    console.log($scope.classes);
}

function pageprocessorMarkAttendance(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    $scope.locationVerified = false;
    $scope.markattendance = false; 
    $scope.attendancereport = [];
    $scope.coordinates = "";
    $scope.debugMode = false;
    
    $scope.verifycurrentlocation = function(schedule) {
        // bypass location check for emulator for testing
        if (!window.device) {
            $scope.locationVerified= true;
            $scope.markClassAttendance(schedule);
            return;           
        }
        $scope.debugMode = true;
        /////////
        $scope.locationVerified = false;
        var options = {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0
        };
        function success(pos) {
          var crd = pos.coords;
        
          //console.log('Your current position is:');
          //console.log('Latitude : ' + crd.latitude);
          //console.log('Longitude: ' + crd.longitude);
          //console.log('More or less ' + crd.accuracy + ' meters.');
          var data = { "flat" : crd.latitude , "flng" : crd.longitude, "tlat" : schedule.LATITUDE , "tlng" : schedule.LONGITUDE }
          //console.log(data);
          $scope.coordinates = data;
          $scope.distance = "";
          //var data = { "flat" : 13.0285711 , "flng" : 77.6605801, "tlat" : 13.0282531 , "tlng" : 77.6604483 }
            $http({
                method: "POST",
                url: "https://kryptosda.kryptosmobile.com/kryptosds/geo/distance",
                data: data
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                //alert(data.distance);
                //callback(data, status, headers, config);
                if (data.distance) {
                    $scope.distance = data.distance;
                    if (data.distance <= 75) {
                        $scope.locationVerified = true;
                    } else {
                        $scope.locationVerified = false;
                    }
                } else {
                    $scope.locationVerified = false;
                }
                $scope.markClassAttendance(schedule);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                $scope.locationVerified = false;
                $scope.markClassAttendance(schedule);
                //alert("Error " + data);
            });          
          
        };

        function error(err) {
            $scope.locationVerified = false;
          console.warn('ERROR(' + err.code + '): ' + err.message);
          retVal = false;
        };

        navigator.geolocation.getCurrentPosition(success, error, options);  
    }
    
    
    $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.endpoint, function(data1) {
        $scope.myschedule = data1.myschedulefortoday;
        $scope.currentdate =    data1.currentdatetime[0].CURRENTDATE;
        console.log($scope.myschedule);
        console.log($scope.currentdate);
        
        if ($scope.myschedule.length > 0) {
            $scope.markattendance = true;
            angular.forEach($scope.myschedule, function(value,key) {
                ///////////////////////////////////////////////////////////////////
                // Get co-ordinates for campus
                //////////////////////////////////////////////////////////////////
                if (!value.LATITUDE || !value.LONGITUDE) {
                    if (value.STVCAMP_DESC && value.STVCAMP_DESC == 'Gardner') {
                        value.LATITUDE = "42.593287600";
                        value.LONGITUDE = "-71.983985500";
                    } else if (value.STVCAMP_DESC && value.STVCAMP_DESC == 'Leominster') {
                        value.LATITUDE = "42.547944600";
                        value.LONGITUDE = "-71.757604000";
                    } else if (value.STVCAMP_DESC && value.STVCAMP_DESC == 'Devens') {
                        value.LATITUDE = "42.537022300";
                        value.LONGITUDE = "-71.628861400";
                    }                    
                }
                //////////////////////////////////////////////////////////////////
                $scope.expectedHours = 0;
                $rootScope.getAPI($rootScope.middlewareServerUrl + '/services/student/bannerstudattendanceinfo/meet_id=' + value.MEET_ID, function(data) {
                    console.log("att info:");
                    console.log(data);
                    if (!value.MINUTES) {
                        console.log("minutes="+value.MINUTES);
                        console.log("def hrs="+data.defaultexpectedhours.length);
                        if (data.defaultexpectedhours.length > 0) {
                            console.log("exp hrs > 0");
                            //var defExpectedHrs = data.defaultexpectedhours[0].EXPECTED_HOURS;
                            //$scope.expectedHours = defExpectedHrs;
                        } else {
                            console.log("exp hours = 0");
                            $scope.markattendance = false;
                            $scope.message = "You cannot enter attendance until your faculty enters Expected hours for the class meeting."  ;
                            return;
                        }                       
                    } 

                    //console.log("step 2");
                    //console.log(data.studattendanceinfo);
                    
                    if (data.studattendanceinfo.length > 0) {
                        //var expectedHrs = data.studattendanceinfo[0].EXPECTED_HOURS;
                        /*
                        if (!expectedHrs) {
                            $scope.markattendance = false;
                            $scope.message = "You cannot enter attendance until your faculty enters Expected hours for the course.";
                            return;
                        }
                        */
                        var attend_ind = data.studattendanceinfo[0].ATTEND_IND;
                        if (attend_ind && attend_ind == 'Y') {
                            $scope.markattendance = false;
                            $scope.message = "You have already marked attendance for the class.";
                            return;                             
                        }
                    } 
                });             
                return;
            });
            
        } else {
            $scope.markattendance = false;
            $scope.message = "You have no classes now to mark attendance.";
        }
    }); 

    $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/bannerstudentattendancereport", function(report){
        $scope.attendancereport = report.studentattendancereport;
        console.log(report);
    });
    
    $scope.markClassAttendance = function(schedule) {
        //$scope.verifycurrentlocation();
        if (!$scope.locationVerified) {
            if (window.device) {
                navigator.notification.alert("You are not verified to be in the class premises !", null, "Mark Attendance", null);
            } else {
                apprise("You are not verified to be in the class premises !", {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {

                });
            }
            return;                     
        }
        $scope.markattendance = false;
        $scope.message= "";
         var postdata = "stud_pidm=" + schedule.PIDM + "&meet_id=" +schedule.MEET_ID + "&minutes=" + schedule.MINUTES;
        $rootScope.postAPI($rootScope.middlewareServerUrl + "/services/student/bannermarkstudattendance", postdata, function (data) {
            if (window.device) {
                var result = JSON.parse(data.p_out[0].summary);
                data.p_out[0].summary = result;
            }
            $scope.attresults = data.p_out[0];
            console.log(data.p_out[0]);
            if ($scope.attresults.summary && $scope.attresults.summary[0].success) {
                $scope.message = $scope.attresults.summary[0].success;
            } else if ($scope.attresults.summary && $scope.attresults.summary[0].error) {
                $scope.message = $scope.attresults.summary[0].error;
            } else {
                $scope.message = "System error. Please contact administrator.";
            }
            $scope.markattendance = false;
            //$scope.message = "";
        });
        
    }
    
    var dv = '<div style="padding:0px;border-radius:0px;"><div class="toolbar-top"><div class="btn-group justified nav-tabs my-nav-tabs">' +
        '<a class="btn btn-default btn-top" href="#Tab1" toggle="on" active-class="active" active><i class="fa fa-pencil-square-o fa-2x"></i><br/>Today\'s Classes</a>' +
        '<a class="btn btn-default btn-top" href="#Tab2" toggle="on" active-class="active"><i class="fa fa-list-alt fa-2x"></i><br/>Attendance Details</a>' +
        '</div> </div></div>';
    $(".app-content").prepend($compile(dv)($scope));
    
}

function pageprocessorAttendanceVerificationPage2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    $scope.class = $rootScope.class;
    $scope.classlist = $rootScope.classlist;
    $scope.selectedDateFormatted = $rootScope.selectedDateFormatted;
    $scope.selectedDateString = $rootScope.selectedDateString;

    angular.forEach($scope.classlist, function(value,key) {
        if (value.ATTENDED == 'Y' && value.DATA_ORIGIN && value.DATA_ORIGIN.indexOf("S") != -1) {
            value.student_class = "student_marked";
        } else {
            value.student_class = "";
        }
/*        if (value.SPRIDEN_ID == '00030950') {
            alert(value.student_class);
        }*/
        //faculty 
        if (value.ATTENDED == 'Y' && value.DATA_ORIGIN && value.DATA_ORIGIN.indexOf("F") != -1 ) {
            value.verifyclass = "btn-success fa fa-check";
        } else {
            value.verifyclass = "btn-danger fa fa-times";           
        }   

    });

    $scope.markAllPresent = function() {
        var studentVerified = "";
        angular.forEach($scope.classlist, function(value,key) { 
            studentVerified = "";
            if (value.DATA_ORIGIN.indexOf("S") != -1) {
                studentVerified = "S";
            } 
            value.ATTENDED = 'Y';
            value.verifyclass = 'btn-success fa fa-check';
            value.DATA_ORIGIN = studentVerified + "F";         
        });
    }

    $scope.markAllAbsent = function() {
        var studentVerified = "";
        angular.forEach($scope.classlist, function(value,key) { 
            studentVerified = "";
            if (value.DATA_ORIGIN.indexOf("S") != -1) {
                studentVerified = "S";
            } 
            value.ATTENDED = 'N';
            value.verifyclass = 'btn-danger fa fa-times';
            value.DATA_ORIGIN = studentVerified;         
        });
    }

    $scope.toggleattendance = function(student) {
        var studentVerified = "";
        if (student.DATA_ORIGIN.indexOf("S") != -1) {
            studentVerified = "S";
        }
        if (student.ATTENDED == 'Y' ) {
            student.ATTENDED = 'N';
            student.verifyclass = 'btn-danger fa fa-times'; 
            student.DATA_ORIGIN = studentVerified;      
        } else {
            student.ATTENDED = 'Y';
            student.verifyclass = 'btn-success fa fa-check';
            student.DATA_ORIGIN = studentVerified + "F";
        }
    }

    $scope.updateAttendance = function() {
        console.log($scope.classlist);
        var endpoint = $rootScope.middlewareServerUrl + "/services/student/bannerfacultymarkattendance";
        var pidms = "";
        var att_inds = "";
        var rowcount = 0;
        angular.forEach($scope.classlist,function(value,key) {
            if (rowcount > 0) {
                pidms = pidms + ",";
                att_inds = att_inds + ",";
            }
            pidms = pidms + value.STUD_PIDM;
            att_inds = att_inds + value.ATTENDED;
            rowcount += 1;
        });
        var attDate = $scope.selectedDateFormatted;

        var postdata = "stud_pidm=" + pidms + "&attend_ind=" + att_inds + "&attend_date=" + attDate + "&meet_id=" + $scope.class.SURROGATE_ID + "&minutes="+ $scope.class.MINUTES;

        $rootScope.postAPI(endpoint, postdata, function (data) {
            console.log(data);
            if (window.device) {
                navigator.notification.alert("The attendance data saved successfully !", null, "Attendance Verification", null);
            } else {
                apprise("The attendance data saved successfully !", {
                    'verify': false,
                    'textYes': "Ok"
                }, function (r) {

                });
            }                                                 
        });         
    }

    $scope.deleteAttendanceData = function() {
        var mendpoint = $rootScope.middlewareServerUrl + "/services/student/bannerdeleteattendancedata";
        var mpostdata = "meet_id=" + $scope.class.SURROGATE_ID +"&meet_date=" + $scope.selectedDateFormatted;
        console.log("mpostdata="+mpostdata);
        $rootScope.postAPI(mendpoint, mpostdata, function (mdata)  {
            console.log(mdata);
            var mess = mdata.p_out[0].summary;
            if (window.device) {
                var onGoBack = function() {
                    $rootScope.back();
                }
                navigator.notification.alert(mess, onGoBack, "Attendance Verification", null);
                //$location.path("/app/AttendanceVerification");
            } else {
                apprise(mess, {
                    'verify': false,
                    'textYes': "Ok"
                }, function (r) {
                    //$location.path("/app/AttendanceVerification");
                    $rootScope.back();
                });
            }             
        });
    }

    $scope.deleteConfirm = function () {

        var onConfirm = function (buttonIndex) {
            if (buttonIndex == 1) {
                $scope.deleteAttendanceData();
            }
        };
        if (window.device) {
            navigator.notification.confirm(
                'Are you sure you want to delete attendance data for this date?', // message
                onConfirm, // callback to invoke with index of button pressed
                'Drop Attendance Data', // title
                ['Yes', 'No'] // buttonLabels
            );
        } else {
            apprise("Are you sure you want to delete attendance data for this date?", {
                'verify': true,
                'textYes': "Yes",
                'textNo': "No"
            }, function (r) {
                if (r) {
                   $scope.deleteAttendanceData();
                }
            });
        }

    }    
}

function pageprocessorAttendanceVerification(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.facultyschedule = [];
  $scope.currentDate = "";
  $scope.selWeekDay = 0;
  $scope.classesExistForDay = false;


  var uMonths = ['JAN', 'FEB', 'MAR', 'APR', 'MAY', 'JUN', 'JUL', 'AUG', 'SEP', 'OCT', 'NOV', 'DEC'];
  var days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
  $scope.weekdata = [{
    style: "",
    label: "M",
    displayLabel: "M",
    data: []
  }, {
    style: "",
    label: "T",
    displayLabel: "T",
    data: []
  }, {
    style: "",
    label: "W",
    displayLabel: "W",
    data: []
  }, {
    style: "",
    label: "R",
    displayLabel: "T",
    data: []
  }, {
    style: "",
    label: "F",
    displayLabel: "F",
    data: []
  }, {
    style: "",
    label: "S",
    displayLabel: "S",
    data: []
  }, {
    style: "",
    label: "U",
    displayLabel: "S",
    data: []
  }];

  $scope.getSelectedDate = function(day) {
    //curr_week_start_date will be a Monday
    var currDate = new Date($scope.facultyschedule.header_info.curr_week_start_date);
    console.log("sel day is=" + day);
    console.log(currDate);
    currDate.setDate(currDate.getDate() + day);
    console.log("Select day Date is:");
    console.log(currDate);
    $scope.selectedDate = currDate;
    $scope.selectedDateString = currDate.toDateString();
    var formattedDate = "";
    if (currDate.getDate().toString().length == 1) {
      formattedDate = "0" + currDate.getDate().toString() + "-" + uMonths[currDate.getMonth()] + "-" + currDate.getFullYear();
    } else {
      formattedDate = currDate.getDate().toString() + "-" + uMonths[currDate.getMonth()] + "-" + currDate.getFullYear();
    }
    $scope.selectedDateFormatted = formattedDate;
    console.log("Formatted date:");
    console.log(formattedDate);
  }

  $scope.selectDay = function(day) {
    for (var i = 0; i < 7; i++) {
      $scope.weekdata[i].style = "";
    }
    $scope.selWeekDay = day;
    $scope.weekdata[day].style = "btn-selected";
    var vfound = false;
    angular.forEach($scope.facultyschedule.class_list, function(value, key) {
      if (value.MON_DAY == $scope.weekdata[day].label ||
        value.TUE_DAY == $scope.weekdata[day].label ||
        value.WED_DAY == $scope.weekdata[day].label ||
        value.THU_DAY == $scope.weekdata[day].label ||
        value.FRI_DAY == $scope.weekdata[day].label ||
        value.SAT_DAY == $scope.weekdata[day].label ||
        value.SUN_DAY == $scope.weekdata[day].label) {
        vfound = true;
      }
    });
    $scope.classesExistForDay = vfound;
    // Get the actual date for the selected day
    $scope.getSelectedDate(day);
  }

  $scope.loadClasses = function(pDate) {
    var endpoint = "/services/student/bannerfacultyclassschedule";
    var postdata = "p_start_date=" + pDate;
    $rootScope.postAPI($rootScope.middlewareServerUrl + endpoint, postdata, function(data) {
      console.log(data);
      if (data.p_out[0] && data.p_out[0].summary) {
        if (window.device) {
          var result = JSON.parse(data.p_out[0].summary);
          data.p_out[0].summary = result;
        }
        $scope.facultyschedule = data.p_out[0].summary[0];
        var currDate = new Date($scope.facultyschedule.header_info.curr_week_start_date);
        $scope.currentDate = currDate.toDateString();

        $scope.nonSchClassesCount = $scope.facultyschedule.nonscheduled_classes.length;
        var classCount1 = 0;
        angular.forEach($scope.facultyschedule.class_list, function(value, key) {
          if (value.MON_DAY) {
            classCount1++;
          }
          if (value.TUE_DAY) {
            classCount1++;
          }
          if (value.WED_DAY) {
            classCount1++;
          }
          if (value.THU_DAY) {
            classCount1++;
          }
          if (value.FRI_DAY) {
            classCount1++;
          }
          if (value.SAT_DAY) {
            classCount1++;
          }
          if (value.SUN_DAY) {
            classCount1++;
          }
        });
        $scope.schClassesCount = classCount1;
        angular.forEach($scope.facultyschedule.nonscheduled_classes, function(value, key) {
          value.MINUTES = 0;
          value.displayMinutes = false;
        });
      }
      var newDate = new Date();
      var curWeekDay = days[newDate.getDay()];
      if (curWeekDay == "Sunday") {
        $scope.currentWeekDay = 6;
      } else if (curWeekDay == "Monday") {
        $scope.currentWeekDay = 0;
      } else if (curWeekDay == "Tuesday") {
        $scope.currentWeekDay = 1;
      } else if (curWeekDay == "Wednesday") {
        $scope.currentWeekDay = 2;
      } else if (curWeekDay == "Thursday") {
        $scope.currentWeekDay = 3;
      } else if (curWeekDay == "Friday") {
        $scope.currentWeekDay = 4;
      } else if (curWeekDay == "Saturday") {
        $scope.currentWeekDay = 5;
      }
      console.log($scope.currentWeekDay);
      $scope.selectDay($scope.currentWeekDay);
    });
  }


  $scope.loadClasses('');

  $scope.loadPreviousSchedule = function() {
    var vPrevDate = $scope.facultyschedule.header_info.prev_week_start_date;
    $scope.loadClasses(vPrevDate);

  }
  $scope.loadNextSchedule = function() {
    var vNextDate = $scope.facultyschedule.header_info.next_week_start_date;
    $scope.loadClasses(vNextDate);

  }

  $scope.showClassRoster = function(myclass) {
    if (!myclass.MINUTES || myclass.MINUTES <= 0) {
      if (window.device) {
        navigator.notification.alert("You should enter the Class duration in minutes.", null, "Attendance Verification", null);
      } else {
        apprise("You should enter the Class duration in minutes.", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }

    var endpoint = $rootScope.middlewareServerUrl + "/services/student/bannerlistattendanceroster";
    var attDate = "";
    var postdata = "att_date=" + $scope.selectedDateFormatted + "&meet_id=" + myclass.SURROGATE_ID + "&minutes=" + myclass.MINUTES + "&term_code=" + myclass.TERM_CODE + "&crn=" + myclass.CRN;
    console.log(postdata);

    $rootScope.postAPI(endpoint, postdata, function(data) {
      console.log(data);
      if (window.device) {
        var result = JSON.parse(data.p_out[0].summary);
        data.p_out[0].summary = result;
      }
      $scope.classlist = [];
      $scope.classlist = data.p_out[0].summary[0].roster;
      if ($scope.classlist.length > 0) {
        $rootScope.class = myclass;
        $rootScope.classlist = $scope.classlist;
        $rootScope.selectedDateFormatted = $scope.selectedDateFormatted;
        $rootScope.selectedDate = $scope.selectedDate;
        $rootScope.selectedDateString = $scope.selectedDateString;
        //$rootScope.$apply(function() {
        $location.path("/app/AttendanceVerification/AttendanceVerificationPage2");
        //});
      } else {
        //error
        if (window.device) {
          navigator.notification.alert("Couldn't find class roster !", null, "Attendance Verification", null);
        } else {
          apprise("Couldn't find class roster !", {
            'verify': false,
            'textYes': "Ok"
          }, function(r) {

          });
        }
      }
    });
  }

  $scope.showUnscheduledClassRoster = function(myclass) {
    if (myclass.displayMinutes && (!myclass.MINUTES || myclass.MINUTES <= 0)) {
      if (window.device) {
        navigator.notification.alert("You should enter the Class duration in minutes.", null, "Attendance Verification", null);
      } else {
        apprise("You should enter the Class duration in minutes.", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }
    var mendpoint = $rootScope.middlewareServerUrl + "/services/student/bannergetunscheduledmeetingtime";
    var mpostdata = "meet_id=" + myclass.SURROGATE_ID + "&meet_date=" + $scope.selectedDateFormatted;
    console.log("mpostdata=" + mpostdata);
    $rootScope.postAPI(mendpoint, mpostdata, function(mdata) {
      console.log(mdata);
      // get meeting time from already added records in SORSATR
      if (mdata.getunscheduledmeetingtime.length > 0) {
        myclass.MINUTES = mdata.getunscheduledmeetingtime[0].MINUTES;
        myclass.displayMinutes = false;
      } else {
        if (!myclass.MINUTES || myclass.MINUTES <= 0) {
          myclass.displayMinutes = true;
          if (window.device) {
            navigator.notification.alert("You should enter the Class duration in minutes.", null, "Attendance Verification", null);
          } else {
            apprise("You should enter the Class duration in minutes.", {
              'verify': false,
              'textYes': "Ok"
            }, function(r) {

            });
          }
          return;
        }
