function pageprocessorVerifyLocation(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
	var data1 = $compile($(pagedef.datatemplate))($scope);
	$("#appContent").append(data1);
    $scope.locationVerified = false;
    $scope.schedule = "";

    $scope.locations = [ 
    					{ "location" : "Gardner" , "latitude" : 42.593287600, "longitude" : -71.983985500},
    					{ "location" : "Leominster" , "latitude" : 42.547944600, "longitude" : -71.757604000},
    					{ "location" : "Devens" , "latitude" : 42.537022300, "longitude" : -71.628861400}
    				   ]



    $scope.verifycurrentlocation = function() {
        // bypass location check for emulator for testing
        if (!window.device) {
            $scope.locationVerified= true;
            return;           
        }
        if (!$scope.selectedvalue) {
        	return;
        }
        //var schedule = { "latitude" : 12.9690161 , "longitude"  : 77.6362811 }
        //var schedule = { "latitude" : 28.4312420 , "longitude"  : 77.0185420 }

        $scope.debugMode = true;
        /////////
        $scope.locationVerified = false;
        var options = {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0
        };
        function success(pos) {
          var crd = pos.coords;
        

          var data = { "flat" : crd.latitude , "flng" : crd.longitude, "tlat" : $scope.schedule.latitude , "tlng" : $scope.schedule.longitude }
          //console.log(data);
          $scope.coordinates = data;
          $scope.distance = "";
          //var data = { "flat" : 13.0285711 , "flng" : 77.6605801, "tlat" : 13.0282531 , "tlng" : 77.6604483 }
            $http({
                method: "POST",
                url: "https://kryptosda.kryptosmobile.com/kryptosds/geo/distance",
                data: data
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                //alert(data.distance);
                //callback(data, status, headers, config);
                if (data.distance) {
                    $scope.distance = data.distance;
                    if (data.distance <= 75) {
                        $scope.locationVerified = true;
                    } else {
                        $scope.locationVerified = false;
                        navigator.notification.alert("You are not verified to be in the class premises !", null, "Check Location", null);
                    }
                } else {
                    $scope.locationVerified = false;
                }

            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                $scope.locationVerified = false;
                navigator.notification.alert("You are not verified to be in the premises !", null, "Check Location*", null);
                //alert("Error " + data);
            });          
          
        };

        function error(err) {
            $scope.locationVerified = false;
          console.warn('ERROR(' + err.code + '): ' + err.message);
          retVal = false;
        };

        navigator.geolocation.getCurrentPosition(success, error, options);  
    }	

  $scope.dropdownselect = function(selectedvalue) {
  	$scope.schedule = "";
	  $scope.coordinates = "";
	  $scope.distance = "";  	
    $('#locselect').blur();
    if ($scope.selectedvalue == "Gardner") {
    	$scope.schedule = $scope.locations[0];
    } else if ($scope.selectedvalue == "Leominster") {
    	$scope.schedule = $scope.locations[1];
    } else if ($scope.selectedvalue == "Devens") {
    	$scope.schedule = $scope.locations[2];
    }
    $scope.verifycurrentlocation();
  }


}

