function pageprocessorVideos(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    $.blockUI();
    $http.get(pagedef.url).success(function(data) {
        console.log(JSON.stringify(data));
        $scope.videos = data.items;
        $rootScope.videos = data.items;
        var data1 = $compile($(pagedef.datatemplate))($scope);
        $("#appContent").append(data1);
        $.unblockUI();
    }).error(function(data) {
        $.unblockUI();
    });
}

function pageprocessorVideosVideosDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var video = $rootScope.videos[$routeParams.index];
  var videoID = video.id.videoId

  var url = "https://www.googleapis.com/youtube/v3/videos?id=" + videoID + "&key=AIzaSyCe0sXjvLPhHFEYFRe3_2W2N1XINaSWzbk&part=snippet,contentDetails,statistics,status";
  $http.get(url).success(function(data) {

    console.log(JSON.stringify(data));
    $scope.title = data.items[0].snippet.localized.title;

    $scope.trustSrc = function() {
      return $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID);
    };
    $scope.description = data.items[0].snippet.localized.description;

    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

  }).error(function(data) {
    $.unblockUI();
  });
try {
