  }
}

function pageprocessorEmail(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    $.blockUI();
    var casurl = pagedef.casurl;
    var qlssourl = pagedef.qlssourl;
    var homepage = "https://iconnect.mwcc.edu/group/mycampus/home";
    var launchurl = pagedef.casurl + "/cas/login?service=" + homepage;
    var iabRef = window.open(launchurl, "_blank", "location=yes,hidden=yes,clearcache=no,enableViewportScale=yes");
    var iabClose = function(data) {
        $.unblockUI();
        //window.location.href = "index.html#home";
    };
    iabRef.addEventListener("exit", iabClose);
    var hideBlockUi = function() {
        // iabRef.show();
        $.unblockUI();
    };
    var username = $.jStorage.get("username");
    var password = $.jStorage.get("password");
    var cd = "document.getElementById('username').value = '" + username + "';document.getElementById('password').value = '" + password + "';document.getElementsByClassName('btn-submit')[0].click();";
    var cb = function(data) {};
    var loadStop = function(data) {
        iabRef.executeScript({
            code: cd
        }, cb);
        if (data.url.indexOf("login") == -1) {
            hideBlockUi();
            //iabRef.close();
            iabRef = window.open(qlssourl, "_blank", "location=yes,hidden=no,clearcache=no,enableViewportScale=yes");
        }
    };
    iabRef.addEventListener("loadstop", loadStop);
    iabRef.addEventListener('loaderror', function(event) {
        //alert('error: ' + event.message);
    });
    $location.path("/home");
}

